import React, { useState, useEffect } from "react";
import axios from "axios";
import "../App.css";

function Forum() {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({
    title: "",
    content: "",
    author: "",
  });
  const [replyContent, setReplyContent] = useState("");
  const [replyAuthor, setReplyAuthor] = useState("");

  useEffect(() => {
    axios.get("http://localhost:4000/api/posts").then((response) => {
      setPosts(response.data);
    });
  }, []);

  const addPost = () => {
    axios.post("http://localhost:4000/api/posts", newPost).then((response) => {
      setPosts([...posts, response.data]);
      setNewPost({ title: "", content: "", author: "" });
    });
  };

  const addReply = (postId) => {
    axios
      .post(`http://localhost:4000/api/posts/${postId}/replies`, {
        content: replyContent,
        author: replyAuthor,
      })
      .then((response) => {
        const updatedPosts = posts.map((post) =>
          post._id === postId ? response.data : post
        );
        setPosts(updatedPosts);
        setReplyContent("");
        setReplyAuthor("");
      });
  };

  return (
    <div className="App relative">
      <section className="gap-x-8 mb-8 mt-4 rounded-lg h-[auto] md:flex-row md:px-0 md:gap-x-16 bg-[#f8f8f8] w-full border border-b-slate-200">
        <div className="text-center md:w-1/2 w-full h-full mx-auto flex flex-col items-center justify-start md:py-12">
          <div className="w-full md:w-[800px] text-center p-4 md:p-2">
            <h2 className="text-[#4be3a8] md:font-extrabold font-bold text-3xl md:text-5xl pb-2 text-accent">
              Get in touch{" "}
              <span className="text-[#242424]"> to Learn More </span>
            </h2>
            <h2 className="font-light text-2xl hidden md:block md:font-normal text-[#242424]">
              A Place for Productive and Respectful Discussions and Debates.
              Discover New Ideas and Make Meaningful Connections
            </h2>
          </div>
        </div>
      </section>
      <div className="posts w-[60%]">
        {posts.map((post) => (
          <div key={post._id} className="post px-12 py-6">
            <h2>{post.title}</h2>
            <p>{post.content}</p>
            <p>
              <span className="font-bold">Author: {post.author}</span>
              <span>Date: {new Date(post.date).toLocaleDateString()}</span>
            </p>
            <ul>
              {post.replies.map((reply, index) => (
                <li key={index}>
                  {reply.content} - {reply.author} (
                  {new Date(reply.date).toLocaleDateString()})
                </li>
              ))}
            </ul>
            <div className="reply-form">
              <input
                type="text"
                placeholder="Your reply"
                value={replyContent}
                onChange={(e) => setReplyContent(e.target.value)}
              />
              <input
                type="text"
                placeholder="Your name"
                value={replyAuthor}
                onChange={(e) => setReplyAuthor(e.target.value)}
              />
              <button onClick={() => addReply(post._id)}>Reply</button>
            </div>
          </div>
        ))}
      </div>
      <div className="new-post-form">
        <h2>New Post</h2>
        <input
          type="text"
          placeholder="Title"
          value={newPost.title}
          onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
        />
        <textarea
          placeholder="Content"
          value={newPost.content}
          onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
        ></textarea>
        <input
          type="text"
          placeholder="Your name"
          value={newPost.author}
          onChange={(e) => setNewPost({ ...newPost, author: e.target.value })}
        />
        <button onClick={addPost}>Submit</button>
      </div>
    </div>
  );
}

export default Forum;
