import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/actions/userActions";

const Header = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  let navigate = useNavigate();

  const LogoutHandler = async () => {
    dispatch(logout());
    navigate("/login");
  };

  return (
    <header className="flex items-center justify-between shadow-xl bg-white px-4 py-4">
      <Link to="/" className="text-2xl pl-4">
        Problem Solvers
      </Link>

      {/* Hamburger menu */}
      <button
        className="lg:hidden text-gray-900 focus:outline-none"
        onClick={() => setIsMenuOpen(!isMenuOpen)}
      >
        <svg
          className="h-6 w-6 fill-current"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          {isMenuOpen ? (
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M20.293 17.707L12 9.414l-8.293 8.293a1 1 0 0 1-1.414-1.414L10.586 8 3.293.707A1 1 0 0 1 4.707-.707l8.293 8.293 8.293-8.293a1 1 0 1 1 1.414 1.414L13.414 8l7.293 7.293a1 1 0 0 1 0 1.414z"
            />
          ) : (
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M4 6h16v2H4V6zm0 5h16v2H4v-2zm0 5h16v2H4v-2z"
            />
          )}
        </svg>
      </button>

      <div className={`${isMenuOpen ? "block" : "hidden"} lg:flex lg:items-center`}>
        <div className="flex items-center space-x-4">
          <Link to="/practice" className="text-gray-900 hover:text-gray-600">
            Practice
          </Link>

          <Link to="/compete" className="text-gray-900 hover:text-gray-600">
            Compete
          </Link>

          <Link to="/discuss" className="text-gray-900 hover:text-gray-600">
            Discuss
          </Link>

          <Link to="/faq" className="text-gray-900 hover:text-gray-600">
            FAQ
          </Link>
        </div>
        
        <div className="flex items-center space-x-4 ml-auto">
          {userInfo ? (
            <div className="flex items-center">
              <span className="text-gray-900">
                Welcome {userInfo.name.split(" ")[0]}
              </span>
              <span
                onClick={LogoutHandler}
                className="text-gray-900 hover:text-gray-600 cursor-pointer"
              >
                Logout
              </span>
            </div>
          ) : (
            <Link to="/login" className="text-gray-900 hover:text-gray-600">
              Login
            </Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
