import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/actions/userActions";

const Header = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  let navigate = useNavigate();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const LogoutHandler = async () => {
    dispatch(logout());
    navigate("/login");
  };

  return (
    <header className="flex items-center justify-between shadow-xl bg-white px-4 py-4">
      <Link to="/" className="text-2xl pl-4 md:pl-12">
        Problem Solvers
      </Link>

      

      <div className={`${isMenuOpen ? "block" : "hidden"} lg:flex lg:items-center space-x-8`}>
        <div class="lg:flex items-center space-x-8">
          <a href="/practice" class="block lg:inline-block text-gray-900 hover:text-teal-700 hover:font-semibold">
            Practice
          </a>

          <a href="/compete" class="block lg:inline-block text-gray-900 hover:text-teal-700 hover:font-semibold">
            Compete
          </a>

          <a href="/discuss" class="block lg:inline-block text-gray-900 hover:text-teal-700 hover:font-semibold">
            Discuss
          </a>

          <a href="/faq" class="block lg:inline-block text-gray-900 hover:text-teal-700 hover:font-semibold">
            FAQ
          </a>
        </div>
        <span className="text-gray-400 hidden md:inline-block">|</span>
        <div className="flex items-center space-x-8 ml-auto pr-12">
          {userInfo ? (
            <div className="flex items-center space-x-8">
              <span className="text-gray-900 font-semibold uppercase">
                {userInfo.name.split(" ")[0]}
              </span>
              <span
                onClick={LogoutHandler}
                className="text-gray-900 hover:text-white inline-block px-4 py-2 rounded-full border border-gray-300 hover:bg-teal-700 hover:border-transparent hover:text-white transition duration-300 "
              >
                Logout
              </span>
            </div>
          ) : (
            <Link to="/login" className="text-gray-900 hover:text-white inline-block px-4 py-2 rounded-full border border-gray-300 hover:bg-teal-700 hover:border-transparent hover:text-white transition duration-300">
              Login
            </Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
