import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProblemDescr = () => {
  const [problem, setProblem] = useState("");
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [answered, setAnswered] = useState(false);
  const [correct, setCorrect] = useState(true);
  const [ip, setIp] = useState("");

  const ansCheckHandler = () => {
    setAnswered(true);
    if (problem.ans) {
      // Make a request to the ChatGPT API to check the similarity
      axios
        .post(
          "https://api.openai.com/v1/engines/davinci-codex/completions",
          {
            prompt: `Default answer: ${problem.ans}\nUser's answer: ${ip}`,
            max_tokens: 1,
            temperature: 0,
            n: 1,
          },
          {
            headers: {
              Authorization: "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",
              "Content-Type": "application/json",
            },
          }
        )
        .then((res) => {
          const completion = res.data.choices[0].text.trim();
          const similarityThreshold = 0.6; // Adjust as needed
          const isSimilar =
            completion.toLowerCase().includes("correct") &&
            completion.length >= similarityThreshold * "correct".length;
          setCorrect(isSimilar);
        })
        .catch((err) => {
          console.log(err);
          setCorrect(false);
        });
    }
  };

  useEffect(() => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/api/problems/get-problem/${id}`)
      .then((res) => {
        setProblem(res.data);
        setLoading(false);
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }, [id]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="px-14 py-9">
      <h1 className="text-3xl font-semibold">{problem.name}</h1>
      <p className="mt-4 py-4">{problem.statment}</p>
      <input
        className="px-5 py-2 border-2 mt-8 "
        onChange={(e) => setIp(e.target.value)}
      />
      {answered && correct && <div className="text-lg">Correct</div>}
      {answered && !correct && (
        <div className="text-lg">Better Luck Next Time</div>
      )}
      {!answered && (
        <div
          onClick={ansCheckHandler}
          className="cursor-pointer block bg-green-500 hover:bg-green-700 transition-all duration-200 text-white font-bold py-2 px-4 rounded w-[87px] cursor-pointer mt-4"
        >
          Submit
        </div>
      )}
    </div>
  );
};

export default ProblemDescr;
