import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import openai from "openai";

const ProblemDescr = () => {
  const [problem, setProblem] = useState("");
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [answered, setAnswered] = useState(false);
  const [correct, setCorrect] = useState(true);
  const [ip, setIp] = useState("");

  const ansCheckHandler = async () => {
    setAnswered(true);
    if (problem.ans) {
      const defaultAnswer = problem.ans; // Default answer from the database
      const userAnswer = ip; // User's input answer

      const similarityThreshold = 0.8; // Adjust as needed

      try {
        // Call the OpenAI API to compare semantic similarity
        const response = await openai.ChatCompletion(
          {
            engine: "text-davinci-003",
            prompt: `Similarity between:\nDefault Answer: ${defaultAnswer}\nUser's Answer: ${userAnswer}`,
            maxTokens: 1,
            temperature: 0,
            n: 1,
            stop: "\n",
          },
          "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"
        );

        console.log("OpenAI API response:", response); // Debug log

        const similarityScore = response.data.choices[0].text.trim();

        console.log("Similarity Score:", similarityScore); // Debug log

        // Check if the semantic similarity score is above the threshold
        const isSimilar = parseFloat(similarityScore) >= similarityThreshold;
        setCorrect(isSimilar);
      } catch (error) {
        console.error("Error comparing semantic similarity:", error);
        setCorrect(false);
      }
    }
  };

  useEffect(() => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/api/problems/get-problem/${id}`)
      .then((res) => setProblem(res.data))
      .catch((err) => console.log(err));
    setLoading(false);
  }, [id]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="px-14 py-9">
      <h1 className="text-3xl font-semibold">{problem.name}</h1>
      <p className="mt-4 py-4">{problem.statment}</p>
      <input
        className="px-5 py-2 border-2 mt-8"
        onChange={(e) => setIp(e.target.value)}
      />
      {answered && correct && <div className="text-lg">Correct</div>}
      {answered && !correct && (
        <div className="text-lg">Better Luck Next Time</div>
      )}
      {!answered && (
        <div
          onClick={ansCheckHandler}
          className="cursor-pointer block bg-green-500 hover:bg-green-700 transition-all duration-200 text-white font-bold py-2 px-4 rounded w-[87px] cursor-pointer mt-4"
        >
          Submit
        </div>
      )}
    </div>
  );
};

export default ProblemDescr;
