import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
const word2vec = require('word2vec'); // Example library for word embeddings
const modelPath = '/path/to/word2vec/model'; // Path to the pre-trained word embeddings model

const ProblemDescr = () => {
  const [problem, setProblem] = useState("");
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [answered, setAnswered] = useState(false);
  const [correct, setCorrect] = useState(true);
  const [ip, setIp] = useState("");

  const ansCheckHandler = () => {
    setAnswered(true);
    if (problem.ans) {
      // Load the word embeddings model
      word2vec.loadModel(modelPath, (err, model) => {
        if (err) {
          console.error('Error loading word2vec model:', err);
          setCorrect(false);
          return;
        }

        const defaultAnswer = problem.ans; // Default answer from the database
        const userAnswer = ip; // User's input answer

        // Calculate the semantic similarity using the word embeddings model
        const similarity = model.similarity(defaultAnswer, userAnswer);

        const similarityThreshold = 0.8; // Adjust as needed

        // Check if the semantic similarity is above the threshold
        const isSimilar = similarity >= similarityThreshold;
        setCorrect(isSimilar);
      });
    }
  };

  useEffect(() => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/api/problems/get-problem/${id}`)
      .then((res) => setProblem(res.data))
      .catch((err) => console.log(err));
    console.log(problem);
    setLoading(false);
  }, [id, problem]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="px-14 py-9">
      <h1 className="text-3xl font-semibold">{problem.name}</h1>
      <p className="mt-4 py-4">{problem.statment}</p>
      <input
        className="px-5 py-2 border-2 mt-8 "
        onChange={(e) => setIp(e.target.value)}
      />
      {answered && correct && <div className="text-lg">Correct</div>}
      {answered && !correct && (
        <div className="text-lg">Better Luck Next Time</div>
      )}
      {!answered && (
        <div
          onClick={ansCheckHandler}
          className="cursor-pointer block bg-green-500 hover:bg-green-700 transition-all duration-200 text-white font-bold py-2 px-4 rounded w-[87px] cursor-pointer mt-4"
        >
          Submit
        </div>
      )}
    </div>
  );
};

export default ProblemDescr;
