import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/actions/userActions";

const Header = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const [isMenuOpen, setIsMenuOpen] = useState(false);

  let navigate = useNavigate();

  const LogoutHandler = async () => {
    window.confirm("Are you sure");
    {
      dispatch(logout());
      navigate("/login");
    }
  };

  return (
    <header className="navbar flex-row jc-sb shadow-xl">
      <Link to="/" className="nav-logo flex-row ai-c">
        Problem Solvers
      </Link>

      {/* Hamburger menu */}
      <button
        className="block lg:hidden flex items-center justify-end h-full px-4"
        onClick={() => setIsMenuOpen(!isMenuOpen)}
      >
        <svg
          className="h-6 w-6 fill-current text-gray-900"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          {isMenuOpen ? (
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M20.293 17.707L12 9.414l-8.293 8.293a1 1 0 0 1-1.414-1.414L10.586 8 3.293.707A1 1 0 0 1 4.707-.707l8.293 8.293 8.293-8.293a1 1 0 1 1 1.414 1.414L13.414 8l7.293 7.293a1 1 0 0 1 0 1.414z"
            />
          ) : (
            <path
              fillRule="evenodd"
              clipRule="evenodd"
              d="M4 6h16v2H4V6zm0 5h16v2H4v-2zm0 5h16v2H4v-2z"
            />
          )}
        </svg>
      </button>

      <div
        className={`${
          isMenuOpen ? "block" : "hidden"
        } lg:flex lg:items-center lg:w-auto w-full `}
      >
        <div className="nav flex-row jc-sb ai-c lg:flex-grow">
          <Link to="/practice" className="nav-item">
            Practice
          </Link>

          <Link to="/compete" className="nav-item">
            Compete
          </Link>

          <Link to="/discuss" className="nav-item">
            Discuss
          </Link>

          <Link to="/faq" className="nav-item">
            FAQ
          </Link>
          <span className="divider"></span>
        </div>

        <div className="user-block flex-row ai-c">
          {userInfo ? (
            <div>
              <span> Welcome {userInfo.name.split(" ")[0]} </span>
              <span onClick={LogoutHandler} className="logout-btn">
                {" "}
                Logout{" "}
              </span>
            </div>
          ) : (
            <Link to="/login" className="login-btn">
              {" "}
              Login{" "}
            </Link>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;

