import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProblemDescr = () => {
  const [problem, setProblem] = useState("");
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [answered, setAnswered] = useState(false);
  const [correct, setCorrect] = useState(true);
  const [ip, setIp] = useState("");
  const [barWidth, setBarWidth] = useState(0);

  const ansCheckHandler = () => {
    setAnswered(true);
    if (problem.ans) {
      if (problem.ans !== ip) setCorrect(false);
    }
    setBarWidth(100);
  };

  useEffect(() => {
    setLoading(true);
    axios
      .get(`http://localhost:4000/api/problems/get-problem/${id}`)
      .then((res) => setProblem(res.data))
      .catch((err) => console.log(err))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="px-14 py-9">
      <h1 className="text-3xl font-semibold">{problem.name}</h1>
      <p className="mt-4 py-4">{problem.statment}</p>
      <input
        className="px-5 py-2 border-2 mt-8"
        onChange={(e) => setIp(e.target.value)}
      />
      {answered && correct && <div className="text-lg">Correct Answer</div>}
      {answered && !correct && <div className="text-lg">Wrong Answer</div>}
      {!answered && (
        <div>
          <div
            onClick={ansCheckHandler}
            className="block bg-green-500 hover:bg-green-700 transition-all duration-200 text-white font-bold py-2 px-4 rounded w-[87px] cursor-pointer mt-4"
          >
            Submit
          </div>
          {correct && answered && (
            <div className="h-2 bg-green-500 mt-2 rounded">
              <div
                className="h-full bg-green-700 rounded animate-bar"
                style={{ width: `${barWidth}%` }}
              />
            </div>
          )}
          {!correct && answered && (
            <div className="h-2 bg-red-500 mt-2 rounded">
              <div
                className="h-full bg-red-700 rounded animate-bar"
                style={{ width: `${barWidth}%` }}
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ProblemDescr;
